INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (1,1, 2, N'Sofa Coastal 2 chỗ KD1085-18 xanh M2', N'Sofa Coastal gây ấn tượng bằng những đường cong bồng bềnh, theo xu hướng Modern Organic – gần gũi với thiên nhiên mà vẫn hiện đại, thoải mái. Điểm đặc biệt của BST lần này nằm ở sự tỉ mỉ của những người thợ thủ công lành nghề, họ đã hoàn thành những đường may uốn lượn không tì vết, mang đến một thiết kế chỉn chu, "cân" mọi góc nhìn. Cảm giác êm ái và thư thái sẽ là những tính từ đầu tiên được nhắc đến khi trải nghiệm sofa Coastal.', CAST(N'2023-01-14' AS Date), 23000000, 0.1, 'sofa01.jpg', 28, 1);
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (2,1, 1, N'Sofa Miami 2 chỗ vải xám', N'Sofa Miami khoác lên mình một màu xám tinh tế, tối giản, mang đến không gian phòng khách hiện đại, trang nhã. Sofa Miami 2 chỗ vải xám sử dụng khung gỗ bọc vải kết hợp cùng với phần nệm ngồi có thể tháo rời, dễ dàng vệ sinh hiệu quả.', CAST(N'2023-01-15' AS Date), 22000000, 0, 'sofa02.jpg', 10, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (3,1, 3, N'Sofa Dubai 2,5 chỗ hiện đại da Simili Be36', N'Sofa Dubai 2.5 chỗ với đường nét thanh mảnh, mang đến cái nhìn nhẹ nhàng và thanh thoát. Thiết kế sofa 2 chỗ nhưng vẫn mang lại cảm giác chỗ ngồi rộng hơn. Sofa Dubai có 2 màu nâu và kem để chọn lựa phù hợp cho không gian phòng khách hiện đại của gia đình bạn.', CAST(N'2023-01-15' AS Date), 12690000, 0, 'sofa03.jpg', 15, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (4,1, 4, N'Sofa 3 chỗ Osaka mẫu 1 vải 29', N'Sofa 3 chỗ từ bộ sưu tập Osaka mang nét hiện đại và thơ mộng của Nhật Bản, tạo nên một không gian sống độc đáo đầy sang trọng. Sản phẩm có phần chân bằng inox màu gold chắc chắn, phần nệm được bọc vải và có thể tháo rời được. Sofa 3 chỗ Osaka mẫu 1 vải 29 không chỉ mang đến thiết kế tinh tế, sang trọng mà còn tạo cho người ngồi cảm giác thoải mái, dễ chịu.', CAST(N'2023-01-14' AS Date), 16000000, 0.1, 'sofa04.jpg', 28, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (5,1, 5, N'Sofa Jazz 3 chỗ hiện đại da cognac', N'Sofa Jazz 3 chỗ được bao bọc bởi lớp da bò cao cấp đem lại cảm giác mềm mại, êm ái, thư giãn tuyệt vời. Thiết kế hiện đại mang nét đẹp độc đáo từ hình khối thanh lịch, đường nét tinh tế là điểm cộng lớn nhất của sofa Jazz.', CAST(N'2023-01-15' AS Date), 33000000, 0, 'sofa05.jpg', 10, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (6,1, 5, N'Sofa Combo 3 chỗ da Bali 520', N'Sofa Combo 3 chỗ da Bali 520 màu nâu trầm là sự lựa chọn sáng suốt nhất của bạn cho không gian phòng khách thêm ấm áp và êm ái. Đây là mẫu sofa da với phần khung gỗ beech cùng đệm siêu đàn hồi, chân kim loại sơn đen và gold, bọc da tự nhiên rất sang trọng và bền đẹp, phù hợp với không gian tiếp khách sang trọng.', CAST(N'2023-01-15' AS Date), 25000000, 0.3, 'sofa06.jpg', 15, 1); 
