INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (92,3, 3, N'Bàn ăn Lucia', N'Bàn ăn dành tròn ở trên xoay được là một tác phẩm nội thất linh hoạt và độc đáo. Thiết kế tròn với khả năng xoay ở phía trên tạo ra sự linh hoạt trong việc sắp xếp và sử dụng không gian. Chất liệu chất lượng cao và cấu trúc xoay vững chắc giúp bàn không chỉ là một phần của nội thất, mà còn là điểm nhấn sáng tạo, phù hợp cho những không gian sống đa dạng và hiện đại. Đây là sự kết hợp tinh tế giữa tính thực tế và thiết kế độc đáo trong không gian bếp hoặc phòng ăn của bạn.', CAST(N'2022-05-10' AS Date), 23500000, 0.3, 'table23.jpg', 20, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (93,3, 1, N'Bàn ăn Peak hiện đại mặt Ceramic vân mây', N'Bàn ăn Peak hiện đại với bề mặt Ceramic được nhập khẩu từ Ý. Mặt Ceramic có khả năng chịu nhiệt tốt với họa tiết vân mây tinh xảo tạo cảm giác vừa vững chắc, vừa uyển chuyển. Chân bàn được thiết kế theo hình tam giác với các góc cạnh bo tròn hiện đại cùng những đường nét uốn lượn mềm mại. Bàn ăn Peak với tông màu trắng trang nhã sẽ mang đến một không gian sống sang trọng và đẳng cấp.', CAST(N'2022-05-10' AS Date), 43500000, 0.3, 'table24.jpg', 20, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (94,3, 3, N'Bàn trang điểm Mây', N'Bàn trang điểm Mây mang kiểu dáng nữ tính, trang nhã, cộng thêm một chút duyên dáng. Phần hộc kéo tiện lợi sẽ là nơi lưu trữ gọn gàng các vật dụng của những quý cô. Với các chi tiết bọc mây điểm xuyết cho sản phẩm thêm phần tinh tế, độc đáo. Đây sẽ là lựa chọn hoàn hảo cho các gia chủ yêu thích phong cách cổ điển pha lẫn một chút hiện đại. ', CAST(N'2022-05-10' AS Date), 47500000, 0.3, 'table25.jpg', 20, 1); 
