INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (131,7, 7, N'Nệm Sen Việt 1m6', N'Nệm lò xo túi Sen Việt bảo hành 10 năm. Lò Xo Túi 7 vùng chống côn trùng, lỗ thông hơi thoáng mát. Nệm lò xo túi Sen Việt được ra mắt từ năm 2017. Bằng tinh thần sáng tạo dân tộc, kết hợp cùng công nghệ sản xuất lò xo túi, Nệm Ưu Việt đã tạo ra dòng sản phẩm Sen Việt với những đường nét hoa sen Việt Nam được chấm phá trên mặt nệm với mong muốn truyền đi ý nghĩa đạo đức lớn lao, mang một giá trị thẩm mỹ vĩnh hằng của loài hoa đã thấm sâu vào tâm hồn dân tộc.', CAST(N'2022-05-10' AS Date), 47500000, 0.3, 'mat01.jpg', 20, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (132,7, 7, N'Nệm lò xo túi 1m6 Isabelle IT160-4IQ', N'Mousse IQ ôm sát từng đường cong cơ thể, được phân bổ đều trên toàn bộ vùng tiếp xúc với nệm. Hoa văn mặt nệm được may định hình một kim trên dây chuyền công nghệ Italy tạo nên nét hoa văn rất tinh tế sắc sảo. Mặt nệm trên được kết hợp từ mousse IQ và vải dệt kim kháng khuẩn của Châu  u tạo cảm giác êm ái và mềm mại hoàn hảo. Còn mặt nệm dưới được kết hợp từ mousse đàn hồi cao và vải dệt kim kháng khuẩn của Châu  u cũng mang đến cảm giác êm ái và mềm mại.', CAST(N'2022-05-10' AS Date), 17500000, 0.3, 'mat02.jpg', 20, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (133,7, 7, N'Nệm lò xo túi Platinum 1m6', N'Nệm lò xo túi Platinum 1m6 là sự kết hợp hoàn hảo giữa chất lượng và thoải mái. Với kích thước rộng 1m6, nệm này cung cấp không gian đủ cho giấc ngủ thoải mái. Cấu trúc lò xo túi Platinum giúp giảm chấn động và tạo sự hỗ trợ linh hoạt cho cơ thể. Lớp lưới đàn hồi và lớp đệm êm dịu tạo nên trải nghiệm ngủ tốt nhất, đồng thời chất liệu chất lượng cao giữ cho nệm luôn duy trì hình dạng và độ đàn hồi lâu dài. Nệm lò xo túi Platinum 1m6 là lựa chọn hoàn hảo để nâng cao chất lượng giấc ngủ của bạn.', CAST(N'2022-05-10' AS Date), 23500000, 0.3, 'mat03.jpg', 20, 1); 
INSERT INTO san_pham (masp, ma_loaisp, ma_nhaCC, tenSP, mo_ta, ngaynhaphang, don_gia, discount, hinh_anh, sl_ton_kho, tinh_trang) VALUES (134,7, 7, N'Nệm lò xo túi OK 1m8', N'Là sự kết hợp hoàn hảo giữa chất lượng và thoải mái trong giấc ngủ. Với kích thước rộng 1m8, nệm này cung cấp không gian đủ cho giấc ngủ thoải mái. Cấu trúc lò xo túi Platinum giúp giảm chấn động và tạo sự hỗ trợ linh hoạt cho cơ thể. Lớp lưới đàn hồi và lớp đệm êm dịu tạo nên trải nghiệm ngủ tốt nhất, đồng thời chất liệu chất lượng cao giữ cho nệm luôn duy trì hình dạng và độ đàn hồi lâu dài. Nệm lò xo túi Platinum 1m6 là lựa chọn hoàn hảo để nâng cao chất lượng giấc ngủ của bạn.', CAST(N'2022-05-10' AS Date), 25400000, 0.3, 'mat04.jpg', 20, 1); 
